import android.os.Handler;
import android.os.Looper;
import android.util.Log;

import java.io.DataOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.ConnectException;
import java.net.InetSocketAddress;
import java.net.NoRouteToHostException;
import java.net.Socket;
import java.net.SocketTimeoutException;
import java.util.Arrays;
import java.util.Timer;
import java.util.TimerTask;

/**
 * 一个socket client，设计成单例的形式，可进行收发消息，会定时向server端发送心跳包
 * <p>
 * <p>
 * 初始化SocketThread：
 * <blockquote>
 * SocketClient.INSTANCE.initSocket(ip, port)
 * </blockquote>
 * 发送消息：
 * <blockquote>
 * SocketClient.INSTANCE.sendData(Msg)
 * </blockquote>
 * 连接成功的回调：
 * <blockquote>
 * SocketClient.INSTANCE.setOnSocketStatusListener(() -> {...})
 * </blockquote>
 * 接收到消息的回调：
 * <blockquote>
 * SocketClient.INSTANCE.setOnReceiveDataListener(message -> {...})
 * </blockquote>
 */
//TODO: 注意增加网络权限
public enum SocketClient {
    INSTANCE; //通过枚举的方式创建单例
    private static final String TAG = "SocketClient";

    //字节流数据
    public static final byte[] HEARTBEAT = {0x01, 0x00, 0x00, 0x00};
    public static final byte[] BYTE_MSG_1 = {0x02, 0x00, 0x00, 0x00};
    public static final byte[] BYTE_MSG_2 = {0x03, 0x00, 0x00, 0x00};

    //字符流数据
    //....

    private String ip;
    private int port;
    private Socket socket;
    private Thread connectThread;

    private final int heartCycle = 10; //心跳周期(s)
    private OutputStream outputStream; //发送输出流
    private final boolean isReConnect = true; //默认重连
    private Timer timer; //倒计时Timer发送心跳包
    private TimerTask task;
    private final Handler handler = new Handler(Looper.getMainLooper());


    // ========================== callback =========================== //
    public interface OnSocketStatusListener {
        /**
         * 连接成功的回调
         */
        void onConnectSuccess();
    }

    public interface OnReceiveDataListener {
        /**
         * 收到消息的回调
         */
        void onReceiveData(byte[] message);
    }

    public OnSocketStatusListener onSocketStatusListener;
    public OnReceiveDataListener onReceiveDataListener;

    public void setOnSocketStatusListener(OnSocketStatusListener onSocketStatusListener) {
        this.onSocketStatusListener = onSocketStatusListener;
    }

    public void setOnReceiveDataListener(OnReceiveDataListener onReceiveDataListener) {
        this.onReceiveDataListener = onReceiveDataListener;
    }


    // ========================== function =========================== //

    /**
     * 初始化socket
     *
     * @param ip   ip地址，如果使用回环地址，则应是127.0.0.1
     * @param port 自定义通信端口，最大至65535
     */
    public void initSocket(final String ip, final int port) {
        this.ip = ip;
        this.port = port;

        if (socket == null && connectThread == null) {
            connectThread = new Thread(() -> {
                socket = new Socket();
                try {
                    socket.connect(new InetSocketAddress(ip, port), 60_000); //超时时间为2秒
                    if (socket.isConnected()) {
                        Log.d(TAG, "client:服务连接成功");
                        new ReadThread().start(); //开启读写线程
                        if (onSocketStatusListener != null)
                            onSocketStatusListener.onConnectSuccess(); //连接成功的回调
                        Thread.sleep(5_000); //5s后发送心跳包
                        sendHeartbeat();
                    }
                } catch (IOException e) {
                    e.printStackTrace();
                    if (e instanceof SocketTimeoutException) {
                        Log.e(TAG, "client:连接超时，正在重连");
                        releaseSocket();
                    } else if (e instanceof NoRouteToHostException) {
                        Log.e(TAG, "client:该地址不存在，请检查");
                    } else if (e instanceof ConnectException) {
                        Log.e(TAG, "client:连接异常或被拒绝，请检查");
                    }
                } catch (InterruptedException e) {
                    e.printStackTrace();
                }
            });
            connectThread.start(); //启动连接线程
        }
    }

    /**
     * 发送数据
     *
     * @param msg 一个byte数组
     */
    public void sendMessage(byte[] msg) {
        if (socket != null && socket.isConnected()) {
            /*发送指令*/
            new Thread(() -> {
                try {
                    Log.i(TAG, "client:发送自定义指令:" + Arrays.toString(msg));
                    //字节流
                    DataOutputStream out = new DataOutputStream(socket.getOutputStream());
                    out.write(msg);
                    out.flush();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }).start();
        } else {
            Log.e(TAG, "client:socket连接错误,请重试");
        }
    }


    // ========================== private function =========================== //

    /**
     * 读线程
     */
    private class ReadThread extends Thread {
        @Override
        public void run() {
            super.run();
            while (socket != null && socket.isConnected()) {
                try (InputStream in = socket.getInputStream()) {
                    //字节流
                    byte[] buff = new byte[HEARTBEAT.length];
                    while ((in.read(buff)) != -1) {
                        Log.d(TAG, "client:客户端收到的字节流: " + Arrays.toString(buff));
                        //接收到数据，切换主线程，显示数据
                        handler.post(() -> {
                            if (onReceiveDataListener != null) {
                                onReceiveDataListener.onReceiveData(buff);
                            }
                        });
                    }
                } catch (Exception e) {
                    e.printStackTrace();
                    Log.e(TAG, "client:接收总控数据异常");
                }
            }
        }
    }


    /**
     * 定时发送心跳数据
     */
    private void sendHeartbeat() {
        if (timer == null) {
            timer = new Timer();
        }
        if (task == null) {
            task = new TimerTask() {
                @Override
                public void run() {
                    try {
                        Log.i(TAG, "client:发送心跳包:" + Arrays.toString(HEARTBEAT));
                        //字节流
                        DataOutputStream out = new DataOutputStream(socket.getOutputStream());
                        out.write(HEARTBEAT);
                        out.flush();
                    } catch (Exception e) {
                        releaseSocket();
                        e.printStackTrace();
                    }
                }
            };
        }
        timer.schedule(task, 0, 1000 * heartCycle);
    }

    /**
     * 释放资源
     */
    private void releaseSocket() {
        if (task != null) {
            task.cancel();
            task = null;
        }
        if (timer != null) {
            timer.purge();
            timer.cancel();
            timer = null;
        }
        if (outputStream != null) {
            try {
                outputStream.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
            outputStream = null;
        }
        if (socket != null) {
            try {
                socket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
            socket = null;
        }
        if (connectThread != null) {
            connectThread = null;
        }
        /*重新初始化socket*/
        if (isReConnect) {
            Log.e(TAG, "client:连接断开，正在重连");
            initSocket(ip, port);
        }
    }

}

