/**
 * 一个socket server，设计成单例的形式，可进行收发消息
 * <p>
 * <p>
 * 初始化SocketServer：
 * <blockquote>
 * SocketServer.INSTANCE.initSocketServer(port);
 * </blockquote>
 * 发送消息：
 * <blockquote>
 * SocketServer.INSTANCE.sendMessage(Msg)
 * </blockquote>
 */
//TODO: 注意增加网络权限
public enum SocketServer {
    INSTANCE; //通过枚举的方式创建单例
    private static final String TAG = "SocketServer";

    //字节流数据
    public static final byte[] HEARTBEAT = {0x01, 0x00, 0x00, 0x00};
    public static final byte[] BYTE_MSG_1 = {0x02, 0x00, 0x00, 0x00};
    public static final byte[] BYTE_MSG_2 = {0x03, 0x00, 0x00, 0x00};

    private Socket socket;


    // ========================== function =========================== //

    /**
     * 初始化socketServer，监听端口，等待客户端连接
     *
     * @param port 自定义通信端口，最大至65535
     */
    public void initSocketServer(final int port) {
        new Thread(() -> {
            try {
                ServerSocket serverSocket = new ServerSocket(port); //创建ServerSocket
                Log.d(TAG, "server:开启服务器");
                while (true) {
                    Log.d(TAG, "server:等待客户端连接");
                    socket = serverSocket.accept(); //等待客户端连接
                    if (serverSocket.isBound()) {
                        Log.d(TAG, "server:得到客户端连接：" + socket);
                        new ReadThread().start(); //开启读写线程
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }).start();
    }


    /**
     * 通过socket来给客户端发送消息
     *
     * @param msg 字节数组
     */
    public void sendMessage(byte[] msg) {
        if (socket != null) {
            new Thread(() -> {
                try {
                    Log.i(TAG, "server:发送自定义指令:" + Arrays.toString(msg));
                    //字节流
                    DataOutputStream out = new DataOutputStream(socket.getOutputStream());
                    out.write(msg);
                    out.flush();
                    //字符流
//                    PrintWriter out = new PrintWriter(new BufferedWriter(new OutputStreamWriter(socket.getOutputStream())), true);
//                    out.println(msg);
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }).start();
        } else {
            Log.d(TAG, "server:没有客户端连接，无法发送消息");
        }
    }


    // ========================== private function =========================== //

    /**
     * 读线程
     */
    private class ReadThread extends Thread {
        @Override
        public void run() {
            super.run();
            while (socket != null && socket.isConnected()) {
                try (InputStream in = socket.getInputStream()) {
                    //字节流
                    byte[] buff = new byte[HEARTBEAT.length];
                    while ((in.read(buff)) != -1) {
                        Log.d(TAG, "server:收到的字节流: " + Arrays.toString(buff));
                        //此处可增加回调...
                    }
                    //字符流
//                    BufferedReader inBuff = new BufferedReader(new InputStreamReader(in, StandardCharsets.UTF_8));
//                    String line;
//                    while ((line = inBuff.readLine()) != null) {// 读取数据
//                        Log.d(TAG, "server:收到的字符流：" + line);
//                    }
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        }
    }

}

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import java.util.List;

/**
 * 通用RecyclerView适配器
 * <p>
 * <p>
 * 1. 在新的类中直接实现其中的接口即可使用：
 * <blockquote>
 * implements CommonBaseAdapter...
 * </blockquote>
 * 2. 使用匿名内部类的形式创建：
 * <blockquote>
 * CommonBaseAdapter<mType> Adapter = new CommonBaseAdapter<>(list,
 *      new CommonBaseAdapter.OnBindDataInterface<mType>() {...}
 * </blockquote>
 */
public class CommonBaseAdapter<T> extends RecyclerView.Adapter<BaseViewHolder> {

    private final List<T> mData;
    private final OnBindDataInterface<T> mOnBindDataInterface;
    private OnMultiTypeBindDataInterface<T> mOnMultiTypeBindDataInterface;
    private ItemOnClickInterface itemOnClickInterface;


    //单样式列表初始化方法
    public CommonBaseAdapter(List<T> data, OnBindDataInterface<T> bindInterface) {
        mData = data;
        mOnBindDataInterface = bindInterface;
    }

    //多样式列表初始化方法
    public CommonBaseAdapter(List<T> data, OnMultiTypeBindDataInterface<T> bindInterface) {
        mData = data;
        mOnMultiTypeBindDataInterface = bindInterface;
        mOnBindDataInterface = bindInterface;
    }

    //itemOnClickInterface初始化
    public void setItemOnClickInterface(ItemOnClickInterface itemOnClickInterface) {
        this.itemOnClickInterface = itemOnClickInterface;
    }


    @NonNull
    @Override
    public BaseViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater
                .from(parent.getContext())
                .inflate(mOnBindDataInterface.getItemLayoutId(viewType), parent, false);
        return new BaseViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull BaseViewHolder holder, int position) {
        mOnBindDataInterface.onBindData(mData.get(position), holder, getItemViewType(position),
                itemOnClickInterface);
    }

    @Override
    public int getItemCount() {
        return this.mData.size();
    }

    @Override
    public int getItemViewType(int position) {
        if (mOnMultiTypeBindDataInterface != null) {
            return mOnMultiTypeBindDataInterface.getItemViewType(position);
        }
        return 0;
    }


    //单样式item绑定数据的接口，包含一个 布局绑定函数 和 布局中控件的绑定函数
    public interface OnBindDataInterface<T> {
        /**
         * 绑定列表元素的item页面(R.layout.xxx)
         *
         * @param viewType
         * @return
         */
        int getItemLayoutId(int viewType);

        /**
         * 绑定数据(R.id.xxx)，最后一个type参数是多类型item使用的
         *
         * @param item   item.get()
         * @param holder holder.setText(R.id.xxx, "a string")
         * @param type   default 0
         */
        void onBindData(T item, BaseViewHolder holder, int type, ItemOnClickInterface itemOnClickInterface);
    }

    // 多类型item的支持，例如包含head的列表
    public interface OnMultiTypeBindDataInterface<T> extends OnBindDataInterface<T> {
        /**
         * 返回item类型，针对不同的item类型使用不同的布局元素
         *
         * @param position switch(case 0, case 1, case 2...)
         * @return
         */
        int getItemViewType(int position);
    }

    // item的回调接口，当item被点击时，能在其他类中获得相关内容并修改item
    public interface ItemOnClickInterface {
        /**
         * @param position 被点击的item下标
         */
        void onItemClick(int position);
    }

}

import android.util.SparseArray;
