import android.content.Context;
import android.graphics.Canvas;
import android.util.AttributeSet;
import android.view.MotionEvent;

/**
 * 竖直的seekbar，xml文件中直接使用此自定义控件
 */
public class VerticalSeekBar extends androidx.appcompat.widget.AppCompatSeekBar {
    private OnSeekBarChangeListener mOnSeekBarChangeListener;

    public VerticalSeekBar(Context context) {
        super(context);
    }

    public VerticalSeekBar(Context context, AttributeSet attrs) {
        super(context, attrs);
    }

    public VerticalSeekBar(Context context, AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
    }

    public void setOnSeekBarChangeListener(OnSeekBarChangeListener l) {
        mOnSeekBarChangeListener = l;
    }

    protected void onSizeChanged(int w, int h, int oldw, int oldh) {
        super.onSizeChanged(h, w, oldh, oldw);
    }

    @Override
    protected synchronized void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
        super.onMeasure(heightMeasureSpec, widthMeasureSpec);
        setMeasuredDimension(getMeasuredHeight(), getMeasuredWidth());
    }

    protected void onDraw(Canvas c) {
        c.rotate(-90); //旋转画布，实现竖直seekbar
        c.translate(-getHeight(), 0);
        super.onDraw(c);
    }

    void onProgressChanged(int progress) {
        if (mOnSeekBarChangeListener != null) {
            mOnSeekBarChangeListener.onProgressChanged(this, progress, true);
        }
    }

    void onStartTrackingTouch() {
        if (mOnSeekBarChangeListener != null) {
            mOnSeekBarChangeListener.onStartTrackingTouch(this);
        }
    }

    void onStopTrackingTouch() {
        if (mOnSeekBarChangeListener != null) {
            mOnSeekBarChangeListener.onStopTrackingTouch(this);
        }
    }

    private void attemptClaimDrag() {
        if (getParent() != null) {
            getParent().requestDisallowInterceptTouchEvent(true);
        }
    }

    @Override
    public boolean onTouchEvent(MotionEvent event) {
        if (!isEnabled()) {
            return false;
        }
        switch (event.getAction()) {
            case MotionEvent.ACTION_DOWN:
                setPressed(true);
                onStartTrackingTouch();
                break;
            case MotionEvent.ACTION_MOVE:
                attemptClaimDrag();
                int len = getMax() - getMin();
                int num = (getMax() - (int) (len * event.getY() / getHeight())); //根据触摸调整进度条
                num = (num > getMax()) ? getMax() : num; //num的值最大为getMax()
                num = (num < getMin()) ? getMin() : num; //num的值最小为getMin()
                setProgress(num);
                onProgressChanged(num);
                break;
            case MotionEvent.ACTION_UP:
            case MotionEvent.ACTION_CANCEL:
                onStopTrackingTouch();
                setPressed(false);
                break;
        }
        return true;
    }
}

import android.app.AlarmManager;
import android.app.Application;
import android.app.PendingIntent;
import android.content.Context;
import android.content.Intent;
import android.os.Build;
import android.util.Log;

/**
 * 捕获程序漏掉的崩溃异常，并重启应用或服务
 * <p>
 * 在application中使用如下方式调用：
 * 重启Activity:
 * <blockquote>
 * Thread.setDefaultUncaughtExceptionHandler(new UnCeHandler(this, Activity.class, UnCeHandler.TYPE_ACTIVITY));
 * </blockquote>
 * 单独重启Service:
 * <blockquote>
 * Thread.setDefaultUncaughtExceptionHandler(new UnCeHandler(this, Service.class, UnCeHandler.TYPE_SERVICE));
 * </blockquote>
 */
public class UnCeHandler implements Thread.UncaughtExceptionHandler {
    public static final String TAG = "UnCeHandler";
    public static final int TYPE_ACTIVITY = 0x0;
    public static final int TYPE_SERVICE = 0x1;

    private static final long DELAY_CLOSE_TIME = 2_000; //延迟杀掉应用的时间
    private static final long REBOOT_TIME = 1_000; //重启应用的时间
    private final Thread.UncaughtExceptionHandler mDefaultHandler;
    private final Application application;
    private final Context mContext;
    private final Class<?> cls;
    private final int selectType;


    // ===================== init ============================== //
    public UnCeHandler(Application application, Class<?> cls, int type) {
        mDefaultHandler = Thread.getDefaultUncaughtExceptionHandler();
        this.application = application;
        this.mContext = application.getApplicationContext();
        this.cls = cls;
        this.selectType = type;
    }


    // ===================== Override ============================== //
    @Override
    public void uncaughtException(Thread thread, Throwable ex) {
        if (!handleException(ex) && mDefaultHandler != null) {
            //如果用户没有处理则让系统默认的异常处理器来处理
            mDefaultHandler.uncaughtException(thread, ex);
        } else {
            delayClose(); //延迟指定时间

            Intent intent = new Intent(mContext, cls);
            PendingIntent restartIntent;
            AlarmManager mgr;
            switch (selectType) {
                //重启service服务
                case TYPE_SERVICE:
                    if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                        /**
                         * PendingIntent.getForegroundService(context, requestCode, intent, flags)
                         *
                         * 第一个参数：连接上下文的context
                         * 第二个参数：是对PendingIntent的描述，请求值不同Intent就不同
                         * 第三个参数：是一个Intent对象，包含跳转目标
                         * 第四个参数有4种状态：
                         *      FLAG_CANCEL_CURRENT: 如果有多个requestCode，则使用最新的PendingIntent对象
                         *      FLAG_NO_CREATE: 如果有相同的PendingIntent存在，直接使用，否则返回null，不会自动创建新的
                         *      FLAG_ONE_SHOT: 该PendingIntent只作用一次，触发后会销毁，再次触发会返回SendIntentException
                         *      FLAG_UPDATE_CURRENT: 如果有一个相同的PendingIntent，则更新之前的数据，使旧的和新的数据一致
                         *      常用的为 FLAG_CANCEL_CURRENT 和 FLAG_UPDATE_CURRENT
                         */
                        restartIntent = PendingIntent.getForegroundService(
                                mContext, 0, intent, PendingIntent.FLAG_UPDATE_CURRENT);
                    } else {
                        restartIntent = PendingIntent.getService(
                                mContext, 0, intent, PendingIntent.FLAG_UPDATE_CURRENT);
                    }
                    break;
                //重启Activity
                case TYPE_ACTIVITY:
                default:
                    restartIntent = PendingIntent.getActivity(
                            mContext, 0, intent, PendingIntent.FLAG_UPDATE_CURRENT);
                    break;
            }
            mgr = (AlarmManager) application.getSystemService(Context.ALARM_SERVICE);
            mgr.set(AlarmManager.RTC, System.currentTimeMillis() + REBOOT_TIME, restartIntent);
            android.os.Process.killProcess(android.os.Process.myPid());
        }
    }


    // ===================== function ============================== //

    /**
     * 延迟指定时间关闭
     */
    private void delayClose() {
        if (DELAY_CLOSE_TIME > 0) {
            try {
                Thread.sleep(DELAY_CLOSE_TIME);
            } catch (InterruptedException e) {
                Log.e(TAG, "error: ", e);
            }
        }
    }

    /**
     * 自定义错误处理，收集错误信息 发送错误报告等操作均在此完成
     *
     * @param ex
     * @return true:处理了该异常信息，否则返回false
     */
    private boolean handleException(Throwable ex) {
        if (ex == null) {
            return false;
        }
        //TODO：程序退出前会先运行此函数，可以在此处理异常信息
        // (无法做UI类的操作，因为程序已经运行不下去了，自测Toast会无效，但是log.d有用，文件读写是否有用有待测试)
        Log.d(TAG, "handleException: " + ex.toString());
        return true;
    }

}

import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentActivity;
import androidx.fragment.app.FragmentManager;
import androidx.fragment.app.FragmentTransaction;

import com.example.androiddemoapp.R;

import java.util.List;

/**
 * 控制Fragment增加、清除、替换等行为
 */
public class mFragmentManage {

    private final FragmentActivity activity;
    private final FragmentManager fragmentManager;

    public mFragmentManage(FragmentActivity activity) {
        this.activity = activity;
        fragmentManager = activity.getSupportFragmentManager();
    }

    
    /**
     * 清空fragmentList的所有Fragment，替换成新的Fragment
     *
     * @param fragment
     */
    public void replaceFragment(Fragment fragment) {
        FragmentManager fragmentManager = activity.getSupportFragmentManager();
        FragmentTransaction transaction = fragmentManager.beginTransaction();
        transaction.replace(R.id.main_fragment_frame, fragment);
        transaction.commit();
    }

    /**
     * 添加Fragment到FragmentList中
     *
     * @param fragment
     */
    public void addFragment(Fragment fragment) {
        FragmentManager fragmentManager = activity.getSupportFragmentManager();
        FragmentTransaction transaction = fragmentManager.beginTransaction();
        transaction.add(R.id.main_fragment_frame, fragment);
        transaction.commit();
    }

    /**
     * 移除最上层的Fragment
     */
    public int removeFragment() {
        List<Fragment> fragments = fragmentManager.getFragments();
        if ((fragments != null) && (fragments.size() > 1)) { //当fragment栈有多个fragment时，返回函数才会生效
            FragmentTransaction transaction = fragmentManager.beginTransaction();
            transaction.remove(fragments.get(fragments.size() - 1));
            transaction.commit();
            return fragmentManager.getFragments().size() - 1;
        } else {
            return 0;
        }
    }

}

