import android.content.pm.PackageManager;
import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.app.ActivityCompat;
import androidx.core.content.ContextCompat;

import java.util.ArrayList;
import java.util.List;

/**
 * 包含权限申请的BaseActivity，其他类继承后可实现权限申请
 * <p>
 * 申请权限时记得在AndroidManifest.xml中增加对应的权限
 * <p>
 * <p>
 * 调用BaseActivity中的权限申请接口：
 * <pre>
 * super.requestRunTimePermission(string, new PermissionListener() {
 *     @ Override
 *     public void onAllGranted() {
 *         //所有权限都通过后会调用此回调
 *     }
 *
 *     @ Override
 *     public void onGrantedList(List<String> grantedPermission) {
 *         //已通过的权限回调（如果权限全通过，此回调也会被调用一下）
 *     }
 *
 *     @ Override
 *     public void onDeniedList(List<String> deniedPermission) {
 *         //如果存在不通过的，此回调将会被调用
 *     }
 * });
 * </pre>
 */
public abstract class BaseActivity extends AppCompatActivity {

    private final int REQUEST_CODE = 200;
    private PermissionListener mListener;

    /**
     * 设置activity对应的layout id
     *
     * @return R.layout.xxx
     */
    protected abstract int setContentViewId();

    /**
     * 初始化
     */
    protected abstract void myOnCreate();


    /**
     * onCreate
     */
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(setContentViewId());
        myOnCreate();
    }


    /*====================================== 权限相关 ==========================================*/

    /**
     * 权限回调接口
     */
    protected interface PermissionListener {
        void onAllGranted(); //授权成功

        void onGrantedList(List<String> grantedPermission); //授权成功的列表

        void onDeniedList(List<String> deniedPermission); //授权失败的列表
    }

    /**
     * 权限申请
     *
     * @param permissions 待申请的权限集合
     * @param listener    申请结果监听事件
     */
    protected void requestRunTimePermission(@NonNull String[] permissions, PermissionListener listener) {
        this.mListener = listener;
        List<String> permissionList = new ArrayList<>();

        //查找未授权的权限，存入集合
        for (String permission : permissions) {
            if (ContextCompat.checkSelfPermission(this, permission) != PackageManager.PERMISSION_GRANTED) {
                permissionList.add(permission); //未授权，则加入待授权的list中
            }
        }
        if (permissionList.isEmpty()) {
            listener.onAllGranted(); //为空，则已经全部授权
        } else {
            //如果集合不为空，则需要去授权
            //使用ActivityCompat请求权限，然后通过Activity的onRequestPermissionsResult函数获得结果的回调
            ActivityCompat.requestPermissions(this, permissionList.toArray(new String[0]), REQUEST_CODE);
        }
    }

    /**
     * Activity的onRequestPermissionsResult函数，当调用ActivityCompat.requestPermissions申请权限后，此函数
     * 会被回调，用户可在此函数中对回调的结果进行处理。
     *
     * @param requestCode  请求码
     * @param permissions  所有的权限集合
     * @param grantResults 授权结果集合
     */
    @Override
    public void onRequestPermissionsResult(int requestCode, @NonNull String[] permissions, @NonNull int[] grantResults) {
        super.onRequestPermissionsResult(requestCode, permissions, grantResults);
        if (requestCode != REQUEST_CODE || grantResults.length <= 0) return;

        List<String> grantedPermissions = new ArrayList<>(); //用户通过的权限集合
        List<String> deniedPermissions = new ArrayList<>(); //被用户拒绝的权限集合

        for (int i = 0; i < grantResults.length; i++) {
            if (grantResults[i] == PackageManager.PERMISSION_GRANTED) {
                grantedPermissions.add(permissions[i]); //用户同意的权限
            } else {
                deniedPermissions.add(permissions[i]); //用户拒绝授权的权限
            }
        }

        //用户拒绝权限为空
        if (deniedPermissions.isEmpty()) {
            mListener.onAllGranted(); //回调所有授权成功的接口
        } else {
            mListener.onDeniedList(deniedPermissions); //回调失败列表的接口
        }
        mListener.onGrantedList(grantedPermissions); //回调成功列表的接口
    }
}

