import java.util.ArrayList;
import java.util.List;

/**
 * 给金额字符串加上单位，中文单位将使用更符合口语的读法，
 * 例如：20302￥（2千03百02元 -> 2千3百02元）
 *       200001000￥（2亿0千0百0十0万1千... -> 2亿01千元）
 * <p>
 * 调用方式：
 * <blockquote>
 * List<String> list = new NumberToRMB().getList( String );
 * </blockquote>
 */
public class NumberToRMB {

    private static final String CHINESE_UNIT = "十亿千百十万千百十元";

    /**
     * 返回符合要求的列表
     *
     * @return
     */
    public List<String> getList(String input) {
        List<String> output = new ArrayList<>();
        output.add("success");
        String integerStr, decimalStr;

        //将整数部分装载到list中
        integerStr = integerAddUnit(input.split("\\.")[0]); //十亿01百万1千01十元
        for (int i = 0, len = integerStr.length(); i < len; i++) {
            char value = integerStr.charAt(i);
            switch (value) {
                case '元': //如果是元，得跳过，因为还有小数要装到list中，不能提前放元到list中
                    break;
                case '十':
                    output.add("ten");
                    break;
                case '百':
                    output.add("hundred");
                    break;
                case '千':
                    output.add("thousand");
                    break;
                case '万':
                    output.add("ten_thousand");
                    break;
                case '亿':
                    output.add("hundred_million");
                    break;
                default:
                    output.add(String.valueOf(value));
            }
        }

        //如果有小数点，将小数部分也装载到list中
        if (input.contains(".")) {
            output.add("dot");
            decimalStr = input.split("\\.")[1];
            output.add(String.valueOf(decimalStr.charAt(0)));
            if (decimalStr.length() > 1) output.add(String.valueOf(decimalStr.charAt(1)));
        }

        output.add("yuan");
        return output;
    }

    /**
     * 将小数点前的金额字符串加上单位
     *
     * @param str
     * @return
     */
    private String integerAddUnit(String str) {
        if (str == null || str.length() < 1) return "0";

        //将值都简单的套上单位，例如: 2023000002 => "2十0亿2千3百0十0万0千0百0十2元"
        StringBuilder temp = new StringBuilder();
        for (int strIndex = str.length() - 1, unitIndex = CHINESE_UNIT.length() - 1;
             strIndex >= 0;
             unitIndex--, strIndex--) {
            temp.append(CHINESE_UNIT.charAt(unitIndex));
            temp.append(str.charAt(strIndex));
        }
        temp.reverse();

        //对上一步的结果进行优化，省去不合理的单位，转换成日常读写的样子，例如：“2十0亿2千3百0十0万0千0百0十2元” => "2十亿2千3百02元"
        String output = temp.toString();
        for (int strLen = str.length(); strLen >= 0; ) {
            if (strLen >= 9) { //数值大于亿
                output = output.replace("0亿", "亿"); // 将类似于 2十0亿 转换成 2十亿
                strLen = 8;
            } else if (strLen >= 5) { //数值大于万且小于亿
                /*
                 * 此处针对类似于：0千0百0十0万、1千0百0十0万、1千1百1十0万 进行优化，优化为 0、1万、1千1百1十万
                 * 其中 0千0百0十0万 情况最为特殊，例如 200'001'000 为 2亿0千0百0十0万1千... 最终化简下来应是
                 * 2亿01千元，如果 0千万 和0十万、0百万一样都替换成 万，则对于上面的例子则会替换成：2亿万1千元，单
                 * 位将产生错误。
                 */
                output = output.replace("0万", "万")
                        .replace("0十万", "万")
                        .replace("0百万", "万")
                        .replace("0千万", "0"); //0千万 要替换为 0 .这个很关键
                strLen = 4;
            } else if (strLen >= 2) { //数值十且小于万
                output = output
                        .replaceAll("0[十百千万]", "0") //将 0十，0百 等相似情况都转换成 0
                        .replaceAll("0+", "0") //在上一步的基础上将 多个连续的零合并，只保留一个
                        .replace("0元", "元") //对 1十0元 等相似情况进行转换，转换成 1十元，注意：0元将不会被转换
                        .replaceAll("^1十", "十"); //将 1十亿元、1十万元、1十元 转换成 十亿元、十万元、十元
                strLen = -1;
            } else {
                strLen = -1;
            }
        }
        return output;
    }

}

import android.content.Context;
import android.content.res.AssetFileDescriptor;
import android.content.res.AssetManager;
import android.media.MediaPlayer;
import android.media.PlaybackParams;
import android.os.Build;

import java.io.IOException;
import java.util.List;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

/**
 * 音频播放类
 * <p>
 * 调用方式：
 * <blockquote>
 * VoiceUtil.getInstance( Context ).play( List );
 * </blockquote>
 */
public class VoiceUtil {

    private static VoiceUtil mVoiceUtil = null;
    private ExecutorService service; //线程池
    private Context mContext;
    private int count;

    /**
     * 创建单例
     *
     * @return
     */
    public static synchronized VoiceUtil getInstance(Context context) {
        if (mVoiceUtil == null) {
            mVoiceUtil = new VoiceUtil(context);
        }
        return mVoiceUtil;
    }

    /**
     * 初始化
     *
     * @param context
     */
    private VoiceUtil(Context context) {
        service = Executors.newCachedThreadPool();
        this.mContext = context;
    }

    /**
     * 播放前检查
     *
     * @param list
     */
    public void play(final List<String> list) {
        if (service != null) { //如果线程池不等于空
            service.execute(new Runnable() {
                @Override
                public void run() {
                    start(list); //list: ["success", "1", "hundred", "yuan"]
                }
            });
        }
    }

    /**
     * 开始播放
     *
     * @param list 要播放的语音列表
     */
    private void start(final List<String> list) {
        synchronized (this) {
            final CountDownLatch latch = new CountDownLatch(1); //并发
            MediaPlayer mediaPlayer = new MediaPlayer(); //安卓自带的播放类

            if (list != null && list.size() > 0) {
                //列表长度和标记位
                final int listLen = list.size();
                count = 0;
                //装载第一个播放项
                AssetManager manager = mContext.getAssets(); //可以读取assets文件中资源的类
                AssetFileDescriptor fd = null;
                try {
                    String path = String.format("tts/tts_%s.mp3", list.get(count));
                    fd = manager.openFd(path);
                    /*
                     * setDataSource参数说明：
                     * 第一个参数：FileDescriptor：您要播放的文件的FileDescriptor
                     * 第二个参数：long：要播放的数据开始的文件的偏移量，以字节为单位
                     * 第三个参数：long：要播放的数据的字节长度
                     */
                    mediaPlayer.setDataSource(fd.getFileDescriptor(), fd.getStartOffset(), fd.getLength());
                } catch (IOException e) {
                    e.printStackTrace();
                    latch.countDown(); //发出当前线程的完成信号
                } finally {
                    if (fd != null) {
                        try {
                            fd.close(); //关闭文件
                        } catch (IOException e) {
                            e.printStackTrace();
                        }
                    }
                }
                mediaPlayer.prepareAsync(); //通过异步的方式加载

                //开始播放
                mediaPlayer.setOnPreparedListener(new MediaPlayer.OnPreparedListener() {
                    @Override
                    public void onPrepared(MediaPlayer mediaPlayer) {
                        mediaPlayer.start();
                    }
                });

                //播放结束的回调
                mediaPlayer.setOnCompletionListener(new MediaPlayer.OnCompletionListener() {
                    @Override
                    public void onCompletion(MediaPlayer mediaPlayer) {
                        mediaPlayer.reset(); //重置播放状态
                        count++;
                        //加载下一个音频文件
                        if (count < listLen) {
                            AssetManager nextManager = mContext.getAssets();
                            AssetFileDescriptor nextFd = null;
                            try {
                                String path = String.format("tts/tts_%s.mp3", list.get(count));
                                nextFd = nextManager.openFd(path);
                                mediaPlayer.setDataSource(nextFd.getFileDescriptor(), nextFd.getStartOffset(), nextFd.getLength());
                                mediaPlayer.prepare(); //以同步的方式加载
                                //调整播放速度
                                if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) { //只有当前运行的版本大于6.0(VERSION_CODE=M)才运行此部分
                                    PlaybackParams params = new PlaybackParams(); //调整播放参数的类，仅支持6.0及以上
                                    params.setSpeed(1.2f); //播放速度最高支持到8.0f，即8倍速，其他参数还有控制音量
                                    mediaPlayer.setPlaybackParams(params);
                                }
                            } catch (IOException e) {
                                e.printStackTrace();
                                latch.countDown(); //发出当前线程的完成信号
                            } finally {
                                if (nextFd != null) {
                                    try {
                                        nextFd.close(); //关闭文件
                                    } catch (IOException e) {
                                        e.printStackTrace();
                                    }
                                }
                            }
                        } else {
                            mediaPlayer.release(); //回收流媒体资源
                            latch.countDown(); //发出当前线程的完成信号
                        }
                    }
                });
            }

            try {
                latch.await(); //等待主线程执行完毕，获得开始执行信号
                this.notifyAll();
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
        }
    }
}

import android.os.Environment;
import android.os.StatFs;
import android.util.Log;

import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.File;
import java.io.FileWriter;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.text.SimpleDateFormat;
import java.util.Arrays;
import java.util.Date;
import java.util.Locale;

/**
 * 日志收集类，可收集当前应用的所有日志，并写入文件中，如果放在系统级app中，则可收集到所有应用的log。可配合zip工具类实现
 * log文件压缩。
 * <p>
 * 文件命名格式为：{@code yyyy-MM-dd[HH-mm-ss].txt};日志默认存储路径为：{@code /storage/emulated/0/Logs/}，
 * 建议使用{@code context.getFilesDir()} 或 {@code context.getExternalFilesDir()}。
 * <p>
 * 本类中用到了File类，所以使用的app需要文件读写权限：{@code READ_EXTERNAL_STORAGE} 和
 * {@code WRITE_EXTERNAL_STORAGE}，对于android 11+的系统，则需要权限：{@code MANAGE_EXTERNAL_STORAGE}，
 * 并在权限控制页面手动允许此权限。
 * <p>
 * <p>
 * 调用方式：
 * <blockquote>
 * LoggingCollect.INSTANCE.runThread();
 * </blockquote>
 * <p>
 * 如果需要定制参数，可在runThread()方法调用前使用：
 * <blockquote>
 * initParameter(maxFileSize_MB, logsFolderMaxSize_MB, logsFolder);
 * </blockquote>
 * 例：
 * <blockquote>
 * LoggingCollect.INSTANCE.initParameter(1, 100, context.getFileDir());
 * </blockquote>
 * <p>
 * <p>
 * 此类中还提供其他文件操作类，可单独进行使用：<p>
 * 获取文件夹大小：
 * <blockquote>
 * LoggingCollect.INSTANCE.getFolderSize_MB(File)
 * </blockquote>
 * 获取外部存储空间大小：
 * <blockquote>
 * LoggingCollect.INSTANCE.getStorageAvailableSize(StringPath)
 * </blockquote>
 * 删除最早创建的文件：
 * <blockquote>
 * LoggingCollect.INSTANCE.deleteEarliestFileInFolder(File)
 * </blockquote>
 */
public enum LoggingCollect {
    INSTANCE; //通过枚举的方式创建单例
    private final static String TAG = "LoggingCollect";
    private final static int BUFF_SIZE = 1024; //写入缓冲区的大小（1KB），如果获取到的是系统log，建议调大buff
    private final static int EXTERNAL_STORAGE_SIZE_MB = 1_000; //sd卡存储空间不足1000MB时，删除旧的日志文件，单位MB

    private int maxFileSize_MB = 4; //单个日志文件大小，单位MB
    private long maxFileSize = maxFileSize_MB * 1024 * 1024; //单个日志文件大小，单位B
    private int logsFolderMaxSize_MB = 100 - maxFileSize_MB; //日志文件夹允许的大小，超过将删除旧文件，单位MB
    //日志默认存储路径：/storage/emulated/0/Logs
    private File logsFolder = new File(Environment.getExternalStorageDirectory() + "/Logs");

    //Thread中使用的变量
    private final SimpleDateFormat mFileSdf = new SimpleDateFormat("yyyy-MM-dd[HH-mm-ss]",
            Locale.getDefault()); //日志文件命名格式：时间.txt
    private Thread t = null;
    private File mLogFile = null;
    private BufferedWriter mWriter;
    private FileWriter mFileWriter;


    /**
     * 初始化各项参数，此函数为可选项，如果不调用，会使用默认的值初始化
     *
     * @param maxFileSize_MB       单个log文件存储大小，单位MB
     * @param logsFolderMaxSize_MB log文件夹存储上限，默认100MB
     * @param logsFolder           log存储路径
     */
    public void initParameter(int maxFileSize_MB, int logsFolderMaxSize_MB, File logsFolder) {
        this.maxFileSize_MB = maxFileSize_MB;
        this.maxFileSize = (long) maxFileSize_MB * 1024 * 1024;
        this.logsFolderMaxSize_MB = logsFolderMaxSize_MB;
        this.logsFolder = logsFolder;
    }

    /**
     * 运行log收集线程
     */
    public void runThread() {
        Log.i(TAG, "runThread_Configure: maxFileSize_MB= " + maxFileSize_MB
                + "; logsFolderMaxSize_MB= " + logsFolderMaxSize_MB
                + "; logsFolder= " + logsFolder.getPath());

        if (t == null || !t.isAlive()) {
            try {
                Process exec = Runtime.getRuntime().exec(new String[]{"logcat", "-b", "all"});
                final InputStream in = exec.getInputStream();
                final BufferedReader reader = new BufferedReader(new InputStreamReader(in));

                t = new Thread(() -> {
                    try {
                        synchronized (this) {
                            String line;
                            while (true) {
                                //检查线程是否收到停止信号
                                if (Thread.currentThread().isInterrupted()) {
                                    Log.i(TAG, "runThread: isInterrupted, break while()");
                                    break;
                                }

                                //检查log文件是否存在，不存在就创建新的
                                if (mLogFile == null || !mLogFile.exists()) {
                                    if (!logsFolder.exists()) logsFolder.mkdir();
                                    mLogFile = new File(getNewLogFilePath());
                                    if (!mLogFile.exists()) mLogFile.createNewFile();
                                    mFileWriter = new FileWriter(mLogFile);
                                    mWriter = new BufferedWriter(mFileWriter, BUFF_SIZE); //default buff=1KB
                                }

                                //写入文件
                                if ((line = reader.readLine()) != null && mWriter != null) {
                                    mWriter.write(line);
                                    mWriter.newLine();
                                    mWriter.flush();
                                }

                                //如果文件达到指定大小，结束写入，并将mLogFile设为null
                                if (mLogFile.length() >= maxFileSize) {
                                    Log.i(TAG, "runThread: close file");
                                    if (mWriter != null) mWriter.close();
                                    if (mFileWriter != null) mFileWriter.close();
                                    mLogFile = null;
                                }
                            }
                        }
                    } catch (IOException e) {
                        e.printStackTrace();
                    } finally {
                        try {
                            if (mWriter != null) mWriter.close();
                            if (mFileWriter != null) mFileWriter.close();
                        } catch (IOException e) {
                            e.printStackTrace();
                        }
                        mLogFile = null;
                        Log.i(TAG, "runThread: thread stop");
                    }
                });
                Log.d(TAG, "runThread: start()");
                t.start();
            } catch (IOException e) {
                e.printStackTrace();
            }
        } else {
            Log.i(TAG, "runThread: Thread isAlive, no create");
        }

    }

    /**
     * 停止当前线程
     */
    public void stopThread() {
        if (t == null || !t.isAlive()) {
            Log.i(TAG, "stopThread: no thread need stop");
        } else {
            t.interrupt(); //调用停止信号
            if (t.isInterrupted()) {
                t = null;
                Log.i(TAG, "stopThread: thread is stop");
            } else {
                Log.e(TAG, "stopThread: thread stop to fail !");
            }
        }
    }

    /**
     * 获取以当前时间命名的新文件名路径，如果logs文件夹达到了存储上限，或者存储空间不足，将会先删除旧log文件
     */
    private String getNewLogFilePath() {
        //检测空间是否足够，如果空间不够，先删除旧文件
        while ((getFolderSize_MB(logsFolder) >= logsFolderMaxSize_MB) ||
                (getStorageAvailableSize(logsFolder) <= EXTERNAL_STORAGE_SIZE_MB)) {
            Log.d(TAG, "Need to delete old log file");
            deleteEarliestFileInFolder(logsFolder);
        }
        //创建以时间命名的txt文件，返回该文件的路径
        String data = mFileSdf.format(new Date());
        String logFilePath = logsFolder.getPath() + File.separator + data + ".txt";
        Log.d(TAG, "getNewLogFilePath: " + logFilePath);
        return logFilePath;
    }


    // =========================== File处理类，可单独使用 ================================ //

    /**
     * 获取文件夹的大小，单位MB
     *
     * @param folder a folder File
     */
    public long getFolderSize_MB(File folder) {
        long size = 0;
        if (!folder.exists()) return size;
        File[] fileList = folder.listFiles();
        if (fileList == null || fileList.length == 0) return size;

        for (File f : fileList) {
            //如果是文件，直接加到size上；如果是文件夹，递归里面的文件
            size += f.isDirectory() ? getFolderSize_MB(f) : f.length();
        }
        Log.d(TAG, "getFolderSize_MB: " + (size >> 20) + "MB");
        return (size >> 20); //等同于：size / 1024 / 1024
    }

    /**
     * 获取外部存储可用大小
     *
     * @param folder 一个文件路径，通过此路径可判断当前存储区的剩余空间
     */
    public long getStorageAvailableSize(File folder) {
        StatFs fs = new StatFs(folder.getPath());
        long count = fs.getAvailableBlocksLong(); //获取可用块数
        long size = fs.getBlockSizeLong(); //获取单个块的大小
        long availableSize = (count * size) >> 20; //等同于：count * size / 1024 / 1024
        Log.d(TAG, "getExternalStorageAvailableSize: " + availableSize + "MB");
        return availableSize;
    }

    /**
     * 删除文件夹中创建时间最早的文件
     *
     * @param folder 要删除的文件夹
     */
    public void deleteEarliestFileInFolder(File folder) {
        if (!folder.isDirectory()) return; //不是一个目录，退出
        File[] files = folder.listFiles();
        if (files == null || files.length == 0) return; //目录中为空，退出
        //对File[]的文件日期进行升序排序，小的在第一个
        Arrays.sort(files, (f1, f2) -> { //a lambda : Arrays.sort(files, new Comparator<File>() {
            long diff = f1.lastModified() - f2.lastModified();
            return diff > 0 ? 1 : (diff == 0 ? 0 : -1);
        });
        //删除最早的文件
        for (File f : files) {
            if (f.isFile()) {
                Log.i(TAG, "deleteFile: " + f.getName() + "; delete result: " + f.delete());
                return; //删除，并直接退出函数
            }
        }
        Log.i(TAG, "folder no file, not delete");
    }
    
    /**
     * 压缩文件，压缩后将删除源文件
     *
     * @param file a file type
     */
    public void zipFile(File file) {
        if (file == null || !file.isFile()) {
            Log.e(TAG, "zipFile: not a file !!");
            return;
        }
        String zipPath = file.getPath().replace(FILE_SUFFIX, ".zip");
        Log.d(TAG, "zipFile: zip file path= " + zipPath);
        try {
            ZipUtil.compress(file, new File(zipPath)); //压缩文件
            Log.d(TAG, "zipFile: delete file= " + file.delete());
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

import android.util.Log;

import org.apache.commons.net.ftp.FTPClient;
import org.apache.commons.net.ftp.FTPClientConfig;
import org.apache.commons.net.ftp.FTPFile;
import org.apache.commons.net.ftp.FTPReply;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

/**
 * FTP操作类，可实现ftp的连接，下载和上传等操作。
 * <p>
 * <p>
 * 类中使用了Apache的Commons Net库。需要将jar包放入libs文件夹，然后在app/build.gradle中增加
 * implementation files('libs/CommonsNet的jar包名称') 并sync。
 * <p>
 * <p>
 * 调用方式：
 * <blockquote>
 * FtpHelper ftp = new FtpHelper(hostName, userName, password);<p>
 * ftp.openConnect();
 * </blockquote>
 * <p>
 * <p>
 * 下载文件或文件夹：
 * <blockquote>
 * ftp.downloadFile(remotePath, fileName, localPath);<p>
 * ftp.downloadFolder(remotePath, localPath);
 * </blockquote>
 * <p>
 * <p>
 * 上传文件或文件夹：
 * <blockquote>
 * ftp.uploadFile(localFilePath, remotePath);<p>
 * ftp.uploadFolder(localFolderPath, remotePath);
 * </blockquote>
 * <p>
 * <p>
 * 获取远程的文件列表：
 * <blockquote>
 * mList = ftp.listFile(remotePath);
 * </blockquote>
 */
public class FtpHelper {
    public static final String TAG = "FtpHelper";

    private final String hostName; //服务器地址
    private final String userName; //用户名
    private final String password; //密码
    private final FTPClient ftpClient; //FTP连接


    /**
     * 构造函数
     *
     * @param hostName hostName 服务器名
     * @param userName userName 用户名
     * @param password password 密码
     */
    public FtpHelper(String hostName, String userName, String password) {
        this.hostName = hostName;
        this.userName = userName;
        this.password = password;
        this.ftpClient = new FTPClient();
    }


    /* ========================================== 连接和关闭 =================================== */

    /**
     * 打开FTP服务
     *
     * @throws IOException
     */
    public void openConnect() throws IOException {
        int reply; //服务器响应值

        //连接至服务器
        ftpClient.setControlEncoding("UTF-8"); //中文转码
        ftpClient.connect(hostName);
        reply = ftpClient.getReplyCode();
        if (!FTPReply.isPositiveCompletion(reply)) {
            ftpClient.disconnect(); //断开连接
            throw new IOException("connect fail: reply=" + reply);
        }

        //登录到服务器
        if (userName != null && !userName.equals("")) {
            ftpClient.login(userName, password);
        } else {
            ftpClient.disconnect(); //断开连接
            throw new IOException("connect fail: username is null or empty");
        }
        reply = ftpClient.getReplyCode(); //获取响应值，判断登陆结果
        if (!FTPReply.isPositiveCompletion(reply)) {
            ftpClient.disconnect(); //断开连接
            throw new IOException("connect fail: reply=" + reply);
        } else {
            //获取登录信息
            FTPClientConfig config = new FTPClientConfig(ftpClient.getSystemType().split(" ")[0]);
            config.setServerLanguageCode("zh");
            ftpClient.configure(config);
            //使用被动模式设为默认
//           ftpClient.enterLocalPassiveMode();
//           ftpClient.enterLocalActiveMode();
//           ftpClient.enterRemotePassiveMode();
            ftpClient.setFileType(FTPClient.BINARY_FILE_TYPE); //二进制文件支持
        }
    }

    /**
     * 关闭FTP服务
     *
     * @throws IOException
     */
    public void closeConnect() throws IOException {
        if (ftpClient != null) {
            ftpClient.logout(); //登出FTP
            ftpClient.disconnect(); //断开连接
        }
    }

    /**
     * 获取ftp连接状态
     *
     * @throws IOException
     */
    public boolean isConnect() {
        return ftpClient.isConnected();
    }


    /* ========================================== 下载 =========================================== */

    /**
     * 下载指定名称的文件
     *
     * @param remotePath FTP目录
     * @param fileName   需要下载的文件名
     * @param localPath  本地存储目录
     * @return Result
     * @throws IOException
     */
    public boolean downloadFile(String remotePath, String fileName, String localPath) throws IOException {
        boolean result = false;
        //在本地创建对应文件夹目录
        File localFolder = new File(localPath);
        if (!localFolder.exists()) Log.d(TAG, "downloadFile: mkdirs= " + localFolder.mkdirs());

        //下载指定文件
        ftpClient.changeWorkingDirectory(remotePath); //更改FTP目录
        FTPFile[] ftpFiles = ftpClient.listFiles(); //得到FTP当前目录下所有文件
        for (FTPFile ftpFile : ftpFiles) {
            if (ftpFile.getName().equals(fileName)) {
                result = downloadSingle(ftpFile, new File(localPath + "/" + ftpFile.getName()));
            }
        }
        return result;
    }

    /**
     * 下载指定目录
     *
     * @param remotePath FTP目录
     * @param localPath  本地存储目录
     * @return Result 成功下载的文件数量
     * @throws IOException
     */
    public int downloadFolder(String remotePath, String localPath) throws IOException {
        int fileCount = 0; //下载的数量
        //在本地创建对应文件夹目录
        File localFolder = new File(localPath);
        if (!localFolder.exists()) Log.d(TAG, "downloadFolder: mkdirs= " + localFolder.mkdirs());

        //循环遍历
        ftpClient.changeWorkingDirectory(remotePath); //更改FTP目录
        FTPFile[] ftpFiles = ftpClient.listFiles(); //得到FTP当前目录下所有文件
        for (FTPFile ftpFile : ftpFiles) {
            if (!ftpFile.getName().equals("..") && !ftpFile.getName().equals(".")) {
                //下载文件夹
                if (ftpFile.isDirectory()) {
                    int count = downloadFolder(remotePath + "/" + ftpFile.getName(), localPath);
                    fileCount += count;
                }
                //下载单个文件
                else if (ftpFile.isFile()) {
                    boolean flag = downloadSingle(ftpFile, new File(localPath + "/" + ftpFile.getName()));
                    if (flag) fileCount++;
                }
            }
        }
        return fileCount;
    }

    /**
     * （private属性，外部不可调用）
     * 下载单个文件，此时ftpFile必须在ftp工作目录下
     *
     * @param ftpFile   FTP文件
     * @param localFile 本地目录
     * @return true下载成功, false下载失败
     * @throws IOException
     */
    private boolean downloadSingle(FTPFile ftpFile, File localFile) throws IOException {
        boolean flag;
        OutputStream outputStream = new FileOutputStream(localFile); //创建输出流
        flag = ftpClient.retrieveFile(ftpFile.getName(), outputStream); //下载单个文件
        outputStream.close(); //关闭文件流
        if (flag) {
            Log.d(TAG, "downloadSingle: success, fileName= " + ftpFile.getName());
        } else {
            Log.e(TAG, "downloadSingle: fail, fileName= " + ftpFile.getName());
        }
        return flag;
    }


    /* ========================================== 上传 =========================================== */

    /**
     * 上传
     *
     * @param localFilePath 需要上传的本地文件路径
     * @param remotePath    FTP目录
     * @return 上传结果
     * @throws IOException
     */
    public boolean uploadFile(String localFilePath, String remotePath) throws IOException {
        boolean flag = false; //返回值
        ftpClient.setFileType(FTPClient.BINARY_FILE_TYPE); //二进制文件支持
        ftpClient.setFileTransferMode(FTPClient.STREAM_TRANSFER_MODE); //设置模式
        ftpClient.changeWorkingDirectory(remotePath); //改变FTP目录
        File localFile = new File(localFilePath);
        if (localFile.exists() && localFile.isFile()) {
            flag = uploadingSingle(localFile);
        }
        return flag;
    }

    /**
     * 上传
     *
     * @param localFolderPath 需要上传的本地文件夹路径
     * @param remotePath      FTP目录
     * @return 上传结果
     * @throws IOException
     */
    public int uploadFolder(String localFolderPath, String remotePath) throws IOException {
        int count = 0;
        ftpClient.setFileType(FTPClient.BINARY_FILE_TYPE); //二进制文件支持
        ftpClient.setFileTransferMode(FTPClient.STREAM_TRANSFER_MODE); //设置模式
        ftpClient.changeWorkingDirectory(remotePath); //改变FTP目录

        File localFolder = new File(localFolderPath);
        if (localFolder.exists() && localFolder.isDirectory()) {
            //在ftp上创建对应的文件夹
            String ftpFolder = remotePath + "/" + localFolder.getName();
            ftpClient.makeDirectory(ftpFolder);
            //改变FTP目录
            ftpClient.changeWorkingDirectory(ftpFolder);
            //遍历文件夹
            File[] files = localFolder.listFiles();
            for (File file : files) {
                //如果是文件夹
                if (file.isDirectory()) {
                    int result = uploadFolder(file.getAbsolutePath(), ftpFolder + "/" + file.getName());
                    count += result;
                }
                //如果是文件
                else if (file.isFile()) {
                    boolean flag = uploadingSingle(file);
                    if (flag) count++;
                }
            }
        }
        return count;
    }

    /**
     * （private属性，外部不可调用）
     * 上传单个文件，需要先改变ftp的目录
     *
     * @param localFile 本地文件
     * @return true上传成功, false上传失败
     * @throws IOException
     */
    private boolean uploadingSingle(File localFile) throws IOException {
        boolean flag;
        InputStream inputStream = new FileInputStream(localFile); //创建输入流
        flag = ftpClient.storeFile(localFile.getName(), inputStream); //上传单个文件
        inputStream.close(); //关闭文件流
        if (flag) {
            Log.d(TAG, "uploadingSingle: success, fileName= " + localFile.getName());
        } else {
            Log.e(TAG, "uploadingSingle: fail, fileName= " + localFile.getName());
        }
        return flag;
    }


    /* ============================================ 其他功能 ====================================== */

    /**
     * 列出FTP指定文件夹下所有文件列表
     *
     * @param remotePath ftp文件夹路径
     * @return FTPFile集合
     * @throws IOException
     */
    public List<FTPFile> listFiles(String remotePath) throws IOException {
        List<FTPFile> list = new ArrayList<>();
        ftpClient.changeWorkingDirectory(remotePath); //切换到指定工作目录
        FTPFile[] files = ftpClient.listFiles();
        Collections.addAll(list, files); //遍历并且添加到集合，list.addAll无法直接添加多个元素，添加数组需要使用Arrays.asList()
        return list;
    }
}

import android.util.Log;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.util.zip.ZipEntry;
import java.util.zip.ZipOutputStream;

/**
 * 文件压缩类，可压缩文件或文件夹，需要放到线程中运行
 * <p>
 * 调用方式：
 * <blockquote>
 * ZipUtil().compress(inFile, outFile);
 * </blockquote>
 */
public class ZipUtil {
    private static final String TAG = "ZipUtil";
    private static final int BUFFER = 4 * 1024; //4KB

    /**
     * 压缩文件或文件夹
     *
     * @param inFile  指向一个文件或目录的file
     * @param outFile 输出的路径+文件名
     */
    public static void compress(File inFile, File outFile) {
        Log.d(TAG, "compressPath= " + inFile.getPath());
        try (
                FileOutputStream outFileStream = new FileOutputStream(outFile);
                ZipOutputStream outZipStream = new ZipOutputStream(outFileStream);
        ) {
            if (inFile.isFile()) {
                FileToZip(inFile, inFile.getName(), outZipStream); //压缩文件
            } else if (inFile.isDirectory()) {
                FolderToZip(inFile, "", outZipStream); //压缩目录
            } else {
                Log.e(TAG, "compress: no file or folder !!");
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        Log.d(TAG, "compress: outFile= " + outFile.getPath());
    }


    /**
     * 压缩单个文件（private属性，外部不可调用）
     *
     * @param file           文件
     * @param zipEntryName   保存时的文件名
     * @param zipOutputSteam OutSteam
     * @throws Exception
     */
    private static void FileToZip(File file, String zipEntryName, ZipOutputStream zipOutputSteam) throws Exception {
        ZipEntry zipEntry = new ZipEntry(zipEntryName); //压缩后的文件名
        FileInputStream inputStream = new FileInputStream(file);
        zipOutputSteam.putNextEntry(zipEntry);
        int len;
        byte[] buffer = new byte[BUFFER];
        while ((len = inputStream.read(buffer)) != -1) {
            zipOutputSteam.write(buffer, 0, len);
        }
        inputStream.close();
        zipOutputSteam.closeEntry();
    }

    /**
     * 压缩目录（private属性，外部不可调用）
     *
     * @param folder         目录
     * @param zipEntryName   保存时的文件名
     * @param zipOutputSteam OutSteam
     * @throws Exception
     */
    private static void FolderToZip(File folder, String zipEntryName, ZipOutputStream zipOutputSteam) throws Exception {
        if (folder.isFile()) {
            FileToZip(folder, zipEntryName, zipOutputSteam);
        } else if (folder.isDirectory()) {
            ZipEntry zipEntry = new ZipEntry(zipEntryName + File.separator);
            zipOutputSteam.putNextEntry(zipEntry);
            zipOutputSteam.closeEntry();

            String[] fileList = folder.list();
            if (fileList != null && fileList.length > 0) {
                File tempFile;
                String tempString;
                for (String s : fileList) {
                    if (s.endsWith(".zip")) continue; //如果是zip文件，则跳过
                    tempFile = new File(folder.getPath() + File.separator + s);
                    tempString = zipEntryName + File.separator + s;
                    FolderToZip(tempFile, tempString, zipOutputSteam);
                }
            }
        } else {
            Log.e(TAG, "FolderToZip: not a file or folder !!");
        }
    }
}

/**
 * 获取assets文件中的数据
 * <p>
 * @param context  getContext()
 * @param fileName "xxx.json"
 * @return String
 */
public static String ReadAssetsFile(Context context, String fileName) {
    StringBuilder stringBuilder = new StringBuilder();
    try {
        AssetManager assetManager = context.getAssets();
        BufferedReader bf = new BufferedReader(
                new InputStreamReader(assetManager.open(fileName)));
        String line;
        while ((line = bf.readLine()) != null) {
            stringBuilder.append(line);
        }
    } catch (IOException e) {
        e.printStackTrace();
    }
    return stringBuilder.toString();
}

/**
 * 将状态栏设置成自定义文字和背景
 * 此效果只对Android 6.0以上有效果
 * <p>
 * 调用方式：
 * <blockquote>
 * setLightMode(getWindow(), R.color.xxx, true);
 * </blockquote>
 * <p>
 * @param win      当前页面窗口
 * @param colorId  颜色id
 * @param darkText 是否使用深色图标和字体
 */
public static void setStatusBarMode(Window win, int colorId, boolean darkText) {
    // 设置状态栏底色
    win.addFlags(WindowManager.LayoutParams.FLAG_DRAWS_SYSTEM_BAR_BACKGROUNDS);
    win.clearFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS);
    win.setStatusBarColor(ContextCompat.getColor(win.getContext(), colorId));

    // 设置状态栏字体是否为黑色
    if (darkText) {
        win.getDecorView().setSystemUiVisibility(View.SYSTEM_UI_FLAG_LIGHT_STATUS_BAR);
    } else {
        win.getDecorView().setSystemUiVisibility(View.SYSTEM_UI_FLAG_VISIBLE);
    }
}

import android.content.Context;
import android.widget.Toast;

/**
 * 防止Toast被多次点击
 * <p>
 * 调用方式：
 * <blockquote>
 * ToastUtil.showShortToast( Context , String );
 * </blockquote>
 */
public class ToastUtil {

    private static Toast toast;

    /**
     * 显示短时间的Toast
     *
     * @param context this
     * @param content 消息提醒的内容
     */
    public static void showShortToast(Context context, String content) {
        if (toast == null) {
            toast = Toast.makeText(context, content, Toast.LENGTH_SHORT);
        } else {
            toast.setText(content);
        }
        toast.show();
    }

    /**
     * 显示长时间的Toast
     *
     * @param context this
     * @param content 消息提醒的内容
     */
    public static void showLongToast(Context context, String content) {
        if (toast == null) {
            toast = Toast.makeText(context, content, Toast.LENGTH_LONG);
        } else {
            toast.setText(content);
        }
        toast.show();
    }
}

/**
 * 在扩展屏中创建一个新页面（双屏异显）
 *
 * @param mContext
 */
public void showToMultipleScreen(Context mContext) {
    DisplayManager displayManager = (DisplayManager) getSystemService(Context.DISPLAY_SERVICE);
    Display[] displays = displayManager.getDisplays(); //获取所有屏幕
    Log.d(TAG, "displays length: " + displays.length);
    if (displays.length > 1 && Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
        MyPresentation myPresentation = new MyPresentation(mContext, displays[1]);
        myPresentation.show();
    }
}

/**
 * Presentation类，实际上是一个Dialog，但是增加了双屏异显的支持
 */
public class MyPresentation extends Presentation {
    private final String TAG = this.getClass().getSimpleName();

    private final Context context;
    private final Display display;


    public MyPresentation(Context outerContext, Display display) {
        super(outerContext, display);
        this.context = outerContext;
        this.display = display;
    }

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.layout_presentation);
        Log.d(TAG, "onCreate: MyPresentation");
    }
}

/**
 * 创建通知，8.0以上的service无法隐式的重启，必须创建通知前台启动
 * <p>
 * 如果是9.0以上的系统，创建前台服务还需要在{@code AndroidManifest.xml}
 * 中增加{@code android.permission.FOREGROUND_SERVICE}权限
 */
private void createNotificationChannel() {
    String CHANNEL_ID = getPackageName();
    String CHANNEL_NAME = TAG; //任意有标记作用的字符串

    if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
        NotificationChannel channel = new NotificationChannel(CHANNEL_ID, CHANNEL_NAME, NotificationManager.IMPORTANCE_HIGH);
        /* 其他可用属性：
        channel.enableLights(true);//设置提示灯
        channel.setLightColor(Color.RED);//设置提示灯颜色
        channel.setShowBadge(true);//显示logo
        channel.setDescription(TAG);//设置描述
        channel.setLockscreenVisibility(Notification.VISIBILITY_PUBLIC); //设置锁屏可见 VISIBILITY_PUBLIC=可见
         */
        NotificationManager manager = (NotificationManager) getSystemService(NOTIFICATION_SERVICE);
        manager.createNotificationChannel(channel);
    }

    Intent intent = new Intent(this, MainActivity.class);
    PendingIntent pendingIntent = PendingIntent.getActivity(this, 0, intent, PendingIntent.FLAG_CANCEL_CURRENT);

    Notification notification = new NotificationCompat.Builder(this, CHANNEL_ID).
            setContentTitle(TAG)
            .setContentText(TAG + " is run...")
            .setWhen(System.currentTimeMillis())
            .setSmallIcon(R.mipmap.ic_launcher) //小图标一定需要设置,否则会报错(如果不设置它启动服务前台化不会报错,但是你会发现这个通知不会启动),如果是普通通知,不设置必然报错
            .setLargeIcon(BitmapFactory.decodeResource(getResources(), R.mipmap.ic_launcher))
            .setContentIntent(pendingIntent).build();
    startForeground(1, notification); //服务前台化只能使用startForeground()方法,不能使用 notificationManager.notify(1,notification); 这个只是启动通知使用的,使用这个方法你只需要等待几秒就会发现报错了
}

